<template>    <div class="video_wrapper">        <div class="video_player">            <div class="col-md-10 offset-md-1 mobile">                    <video-player class="vjs-custom-skin videoPlayer" ref="videoPlayer" :options="playerOptions" :playsinline="true" v-if="video_mes"></video-player>            </div>        </div>        <div class="video_mes_wrapper ">            <div class="video_mes col-md-10 offset-md-1">                    <span class="video_name">{{video_mes.vname}}</span>                    <a :href="video_mes.videoUrl+'?attname='+video_mes.download_name" class="video_download md-hidden">                        <span class="iconfont icon-download"></span>下载视频                    </a>                    <span class="video_date">视频发布于：{{video_mes.created_time}}</span>            </div>        </div>        <comment :discussion_id="discussion_id" :type="type"></comment>    </div></template><script>    import comment from "../comment/comment"    export default {        name: "video",        data() {            return{                playerOptions: {                    // videojs options                    muted: true,                    language: 'zh-CN',                    playbackRates: [0.5, 1.0, 1.5, 2.0],                    preload: 'auto',                    sources: [{                        type: "video/mp4",                        src: null                    }],                    poster: "",                    muted: false,                    controls: true,                    fluid: true                },                video_mes: null,                type: 'v',                discussion_id: this.video_id            }        },        props:['video_id'],        created() {            this.get_video_mes();        },        methods:{            get_video_mes() {                this.axios.get('/weixin/find_video_mes/'+ this.video_id)                    .then(response => (                        this.video_mes = response.data                    )).catch(function (error) {                    console.log(error);                }).finally(() => {                    this.playerOptions.sources[0].src = this.video_mes.videoUrl;                });            }        },        computed: {        },        components: {            comment        }    }</script><style scoped lang="stylus">@import "../../common/stylus/video.stylus"</style>